// Webhook'tan gelen ham veriyi al
const input = items[0].json;

// marketing objesi formdaki "marketing" alanı (HTML'de öyle tanımlamıştık)
const marketing = input.marketing || {};

const highlightsRaw = marketing.highlights || "";
const highlights = highlightsRaw
  .split(",")
  .map(s => s.trim())
  .filter(Boolean);

// Tek bir normalize edilmiş JSON döndürüyoruz
return [
  {
    json: {
      portfolio: {
        location: $input.first().json.body.location || "",
        listingNo: $input.first().json.body.listingNo || "",
        propertyType: $input.first().json.body.propertyType || "",
        price: $input.first().json.body.price || null,
        brut: $input.first().json.body.brut || null,
        net: $input.first().json.body.net || null,
        rooms: $input.first().json.body.rooms || "",
        floor: $input.first().json.body.floor || "",
        heating: $input.first().json.body.heating || "",
        site: $input.first().json.body.site || "",
        parking: $input.first().json.body.parking || "",
        credit: $input.first().json.body.credit || ""
      },
      marketing: {
        target: $input.first().json.body.marketing.target || "",
        tone: $input.first().json.body.marketing.tone || "",
        highlights,
        language: $input.first().json.body.marketing.language || "tr",
        notes: $input.first().json.body.marketing.notes || ""
      }
    }
  }
];

const data = items[0].json;
const p = data.portfolio;
const m = data.marketing;

const loc = p.location || "Lokasyon belirtilmemiş";
const priceText = p.price ? `${p.price} TL` : "fiyat belirtilmemiş";
const highlights = (m.highlights || []).join(", ");

// Dil bilgisi (şimdilik sadece TR mantığıyla yazdık)
const lang = m.language || "tr";

// Portal prompt
const portalPrompt = `
Aşağıdaki özelliklere sahip bir konut için emlak portallarında (Sahibinden vb.)
kullanılmak üzere profesyonel, detaylı ve SEO uyumlu bir ilan açıklaması yaz.

Lütfen:
- Konumu, m² bilgilerini, oda sayısını ve ısıtma türünü net şekilde belirt
- Varsa site içi, otopark, krediye uygunluk gibi avantajları vurgula
- ${m.target || "genel kitle"} hedef kitlesine uygun bir dil kullan
- ${m.tone || "Samimi"} bir ton tercih et
- Gereksiz süslü ifadelerden kaçın, net ve açıklayıcı ol

Portföy Özeti:
- Lokasyon: ${loc}
- Emlak tipi: ${p.propertyType}
- Oda sayısı: ${p.rooms}
- m² (Brüt/Net): ${p.brut || "-"} / ${p.net || "-"}
- Bulunduğu kat: ${p.floor}
- Isıtma: ${p.heating}
- Site içerisinde: ${p.site}
- Otopark: ${p.parking}
- Krediye uygun: ${p.credit}
- Öne çıkan özellikler: ${highlights || "-"}
- Fiyat: ${priceText}

Ek Notlar (danışmanın isteği):
${m.notes || "-"}
`.trim();

// Instagram prompt
const instagramPrompt = `
Aşağıdaki portföy için Instagram gönderi metni yaz.

Lütfen:
- Dikkat çeken bir giriş cümlesi kullan
- Emoji kullan ama abartma
- Sonunda "Detaylar ve konum için DM'den yazın" benzeri bir CTA olsun
- 5 adet alakalı hashtag ekle (#izmir #emlak vb.)

Portföy Özeti:
- Lokasyon: ${loc}
- Emlak tipi: ${p.propertyType}
- Oda sayısı: ${p.rooms}
- m² (Brüt/Net): ${p.brut || "-"} / ${p.net || "-"}
- Öne çıkan özellikler: ${highlights || "-"}
- Fiyat: ${priceText}
- Hedef kitle: ${m.target}
- Ton: ${m.tone}
`.trim();

// LinkedIn prompt
const linkedinPrompt = `
Aşağıdaki portföy için profesyonel bir LinkedIn paylaşım metni yaz.

Lütfen:
- Bu portföyü "yatırım fırsatı" ve "lokasyon avantajı" odağında konumlandır
- Kurumsal ve profesyonel bir dil kullan
- Orta/uzun vadeli değer artışı potansiyeline vurgu yap
- Sonunda "Detaylı bilgi için iletişime geçebilirsiniz" tarzı bir CTA ekle

Portföy Özeti:
- Lokasyon: ${loc}
- Emlak tipi: ${p.propertyType}
- Oda sayısı: ${p.rooms}
- m² (Brüt/Net): ${p.brut || "-"} / ${p.net || "-"}
- Site içerisinde: ${p.site}
- Öne çıkan özellikler: ${highlights || "-"}
- Fiyat: ${priceText}
- Hedef kitle: ${m.target}
`.trim();

// Reels prompt
const reelsPrompt = `
Aşağıdaki portföy için 30–60 saniyelik Instagram Reels / TikTok video senaryosu yaz.

Lütfen:
- Senaryoyu zaman aralıklarıyla yaz (örn. [0–5 sn], [5–15 sn])
- Hangi sahnede ne gösterileceğini ve dış sesin ne söyleyeceğini belirt
- Son bölümde fiyat ve güçlü bir CTA olsun
- Dil: ${m.tone || "Samimi"} ama profesyonel

Portföy Özeti:
- Lokasyon: ${loc}
- Emlak tipi: ${p.propertyType}
- Oda sayısı: ${p.rooms}
- m² (Brüt/Net): ${p.brut || "-"} / ${p.net || "-"}
- Site içerisinde: ${p.site}
- Öne çıkan özellikler: ${highlights || "-"}
- Fiyat: ${priceText}
- Hedef kitle: ${m.target}

Ek Notlar:
${m.notes || "-"}
`.trim();

return [
  {
    json: {
      portfolio: p,
      marketing: m,
      prompts: {
        portal: portalPrompt,
        instagram: instagramPrompt,
        linkedin: linkedinPrompt,
        reels: reelsPrompt
      }
    }
  }
];

// Merge'den gelen 4 item'i tek objeye çeviriyoruz
const contents = {};

for (const item of items) {
  const obj = item.json;
  const [key, value] = Object.entries(obj)[0]; // örn: ["portal", "..."]
  const normalizedKey = key.toLowerCase();
  contents[normalizedKey] = value;
}

// Cache key'i önceki node'dan çek
const cacheKey = $('Önbellek Anahtarını Hesapla').first().json.cacheKey

return [
  {
    json: {
      success: true,
      fromCache: false,
      cacheKey,
      contents
    }
  }
];

// Normalleştirilmiş veriyi al
const data = items[0].json; 
// data = { portfolio: {...}, marketing: {...} }

// Sadece cache için önemli alanlar
const keyObj = {
  portfolio: data.portfolio,
  marketing: data.marketing,
};

// String'e çevir
const raw = JSON.stringify(keyObj);

// Basit bir hash fonksiyonu (32 bit int)
function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const chr = str.charCodeAt(i);
    hash = (hash << 5) - hash + chr;
    hash |= 0; // 32-bit
  }
  return hash.toString();
}

const cacheKey = simpleHash(raw);

return [
  {
    json: {
      ...data,   // portfolio + marketing aynen kalsın
      cacheKey,  // yeni alanımız
    }
  }
];
